import { Component } from '@angular/core';
import { Router } from '@angular/router';

import { lastValueFrom } from 'rxjs';
import { DatePipe } from '@angular/common';

import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SubscriptionService } from 'src/app/Services/subscription/subscription.service';
import { PaymentService } from 'src/app/Services/payment/payment.service';
import { environment, stripeConfig } from 'src/app/environments/environment';
import { StripeCustomer, subscription } from 'src/app/Model/payment/subscription';
import { CommonService } from 'src/app/Services/common/common.service';
import { StorageKey, StorageService } from 'src/app/Services/storage/storage.service';
import { ToastrMessageService } from 'src/app/Services/toastr-service/toastr-message.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { MatDialog } from '@angular/material/dialog';
declare var Stripe: any;

const stripe = Stripe(stripeConfig.publishKey);
@Component({
  selector: 'app-subscriptions',
  templateUrl: './subscriptions.component.html',
  styleUrls: ['./subscriptions.component.css']
})
export class SubscriptionsComponent  {
  registerForm: FormGroup | undefined;
  SubscriptionList: any = [];
  stripeCustomer: StripeCustomer | any;
  searchText: string | undefined;
  CardList: any = [];
  subscriptionModel: subscription | undefined;
  customerId: any;
  CardAddForm: FormGroup | undefined;
  cardToken: any;
  planId: any;
  planName: any;
  charges: number = 0;
  isDefultCard: string | null = null;
  baseURL: string = environment.BaseURL.replace("api/", "");
  endDate: any;
  isActive: boolean = false;
  todayDate: any = this.datePipe.transform(new Date(), 'MMM d, y, h:mm:ss a');
  cancaleDate: any;
  PaymentSecrectKey: any;
  scheduleId: any;

  constructor(
    private toster: ToastrMessageService,
    public commonService: CommonService,
    private spinner: NgxSpinnerService,
    private subscriptionService: SubscriptionService,
    private route: Router,
    private storage: StorageService,
    private paymentService: PaymentService,
    private datePipe: DatePipe,
    private fb: FormBuilder
  ) {
    
  }

  ngOnInit(): void {
    this.GetStripeDetailsById();
    window.scrollTo(0, 0);
    this.GetSubscriptionData();
    this.GetSavedCardList();
  }

  GetStripeDetailsById() {
    this.spinner.show();
    const userId = this.commonService.Decrypt(this.storage.getValue(StorageKey.currentUserId))
    this.subscriptionService.getStripeDetailsById(userId).then((data: any) => {
      if (data != null) {
        this.customerId = data.data.customerId;
        this.planId = data.data.planId;
        this.isActive = data.data.isActive;
        this.scheduleId = data.data.scheduleId;
        this.endDate = this.datePipe.transform(data.data.endDate, 'MMM d, y, h:mm:ss a');
        this.cancaleDate = this.datePipe.transform(data.data.cancelDate, 'MMM d, y, h:mm:ss a');
        if (this.planId != null) {
          this.GetSubscriptionById();
        }
      }
      else {
      }
    }).catch((error: any) => {
      this.spinner.hide();
    })
  }

  GetSubscriptionById() {
    this.spinner.show();
    this.paymentService.GetSubscriptionPlanById(this.planId).then((data: any) => {
      if (data && data.success) {
        this.planName = data.data.planName;
        this.charges = parseInt(data.data.charges);
      }
    }).catch((error: any) => {
      this.toster.error(error.error.message);
    })
  }

  GetSubscriptionData() {
    this.spinner.show();
    const userId = this.commonService.Decrypt(this.storage.getValue(StorageKey.currentUserId))
    this.subscriptionService.GetSubscriptionData(userId).then((data: any) => {
      this.spinner.hide();
      if (data && data.success) {
        this.SubscriptionList = data.data;
      }
      else {
        this.toster.error(data.message);
      }
    }).catch((error: any) => {
      this.spinner.hide();
    });
  }

  GetSavedCardList() {
    this.spinner.show();
    this.customerId = (this.commonService.Decrypt(this.storage.getValue(StorageKey.currentUserId)));
    if (this.customerId != 'null') {
      this.subscriptionService.GetCardDetails(this.customerId).then((data: any) => {
        this.spinner.hide();
        if (data && data.success) {
          this.CardList = data.data.data;
        }
      }).catch((error) => {
        this.spinner.hide();
        this.toster.error(error.error.message);
      });
    }
  }

  onSubmit(id: string) {
    const paymentId = this.commonService.Encrypt(id)
    this.route.navigate(["pay",paymentId])
  }

 

  CancelSubscription() {
    this.spinner.show();
    this.customerId = (this.commonService.Decrypt(this.storage.getValue(StorageKey.currentUserId)))
    this.subscriptionService.CancelSubscription(this.customerId).then((data: any) => {
      this.spinner.hide();
      if (data && data.success) {
        this.toster.success(data.message);
        this.route.navigate(["match"]);
      }
      else {
        this.toster.error(data.message);
      }
    }).catch((error) => {
      this.toster.error(error.error.message);
      this.spinner.hide();
    })
  }

 
}
