import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Stripe_demo';
}

import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NgxSpinnerService } from 'ngx-spinner';

import { lastValueFrom } from 'rxjs';
import { StripeCustomer, subscription } from 'src/app/Model/payment/subscription';
import { CommonService } from 'src/app/Services/common/common.service';
import { PaymentService } from 'src/app/Services/payment/payment.service';
import { StorageKey, StorageService } from 'src/app/Services/storage/storage.service';
import { SubscriptionService } from 'src/app/Services/subscription/subscription.service';
import { ToastrMessageService } from 'src/app/Services/toastr-service/toastr-message.service';
import { environment, stripeConfig } from 'src/app/environments/environment';

declare var Stripe: any;

const stripe = Stripe(stripeConfig.publishKey);



export class PaymentComponent {
  PlanDetails: any = [];
  planId: any;
  customerId: any;
  stripeCustomer: StripeCustomer | any;
  subscriptionModel: subscription | undefined;
  cardList: any = [];
  showAddCard: boolean = true;
  stripeCustomerId: any;
  cardId: any;
  baseURL: string = environment.BaseURL.replace("api/", "");
  todayDate: any = Date.now();
  endDate: any;
  cancaleDate: any;
  PaymentSecrectKey: any;
  isActive: boolean | undefined;

  constructor(private toster: ToastrMessageService,
    public commonService: CommonService,
    private spinner: NgxSpinnerService,
    private route: ActivatedRoute,
    private paymentService: PaymentService,
    private storage: StorageService,
    private subscriptionService: SubscriptionService,
    private router: Router
  ) {

  }

  ngOnInit(): void {
    this.GetStripeDetailsById();
    this.spinner.show();
    this.planId = this.commonService.Decrypt(this.route.snapshot.paramMap.get('id') ?? "");
    this.GetSubscriptionPLanById();
  }

  GetStripeDetailsById() {
    this.spinner.show();
    const userId = this.commonService.Decrypt(this.storage.getValue(StorageKey.currentUserId))
    this.subscriptionService.getStripeDetailsById(userId).then((data: any) => {
      if (data != null) {
        this.stripeCustomerId = data.data.customerId;
        this.endDate = data.data.endDate;
        this.isActive = data.data.isActive;
        this.cancaleDate = data.data.cancelDate;
        if (this.stripeCustomerId == null) {
          this.showAddCard = false;
        }
        this.GetSavedCardList();
      }
      else {
      }
    }).catch((error: any) => {
      this.spinner.hide();
    })
  }

  async LoadAndPay() {
    const result = this.subscriptionService.InitiatePayment();
    this.PaymentSecrectKey = await lastValueFrom(result);
    var elements = new stripe.elements({
      clientSecret: this.PaymentSecrectKey.data.clientSecret,
      fonts: [
        {
          cssSrc: 'https://fonts.googleapis.com/css?family=Source+Code+Pro',
        }
      ]
    });


    var cardNumber = elements.create('cardNumber', {
    });
    cardNumber.mount('#card-number-element');
    var cardExpiry = elements.create('cardExpiry', {
    });
    cardExpiry.mount('#card-cardExpiry-element');
    var cardCvc = elements.create('cardCvc', {
    });
    cardCvc.mount('#card-cardCvc-element');

    document.querySelector("#payment-form")?.addEventListener("submit", async (e) => {
      e.preventDefault();
      this.spinner.show();
      stripe.handleCardSetup(
        this.PaymentSecrectKey.data.clientSecret,
        cardNumber
      )
      .then((result: any) => {
        if (result.setupIntent != undefined && result.setupIntent.status == "succeeded") {
          this.subscriptionModel = {
            UserId: parseInt(this.commonService.Decrypt(this.storage.getValue('currentUserId'))),
            PlanId: this.planId,
            PaymentMethodId: result.setupIntent.payment_method,
          }
          if (this.stripeCustomerId == null || (!this.isActive && this.cancaleDate != null)) {
            this.paymentService.InitializeCustomerSetupandSubscribe(this.subscriptionModel).then((data: any) => {
              this.spinner.hide();
              if (data.success) {
                this.commonService.setData(true);
                this.router.navigate(["match"]);
                this.toster.success("You have successfully subscribed " + this.PlanDetails.planName);
                this.stripeCustomerId = data.data.customer;
              }
              else {
                this.toster.error(data.message);
              }
            }).catch((error: any) => {
              this.toster.error(error.error.message);
              this.spinner.hide();
            })
          } else {
            this.subscriptionService.UpgradeSubscription(this.subscriptionModel).then((data: any) => {
              this.spinner.hide();
              if (data && data.success) {
                this.toster.success(data.message);
                this.router.navigate(["match"]);
              }
              else {
                this.toster.error(data.message);
              }
            }).catch((error) => {
              this.toster.error(error.error.message);
              this.spinner.hide();
            })
          }
        }

        else {
          this.toster.error(result.error.message)
          this.spinner.hide();
        }
      })
    });
  }

  GetSubscriptionPLanById() {
    this.paymentService.GetSubscriptionPlanById(this.planId).then((data: any) => {
      this.spinner.hide();
      if (data && data.success) {
        this.PlanDetails = data.data;
      }
      else {
        this.toster.error(data.message);
      }
    }).catch((error: any) => {
      this.spinner.hide();
      this.toster.error(error.error.message);
    })

  }

  GetSavedCardList() {
    this.customerId = this.commonService.Decrypt(this.storage.getValue('currentUserId'))
    if (this.customerId != 'null') {
      this.spinner.show();
      this.subscriptionService.GetCardDetails(this.customerId).then((data: any) => {
        this.spinner.hide();
        if (data && data.success) {
          this.cardList = data.data.data;
        }
        if (!this.showAddCard) {
          this.LoadAndPay();
        }
      }).catch((error) => {
        this.spinner.hide();
      });
    }
  }

  goToBack() {
    this.router.navigate(["subscription"]);
  }

  showCardList() {
    this.showAddCard = true;
  }

  showCard() {
    this.cardId = null;
    this.showAddCard = false;
    setTimeout(() => {
      this.LoadAndPay();
    }, 0);

  }
  Addclass(id: any) {
    this.cardId = id
    document.querySelectorAll('.selected').forEach(e => {
      e.classList.remove('selected');
    });
    document.getElementById(id)?.classList.add('selected');
  }

  pay() {
    this.spinner.show();
    this.subscriptionModel = {
      UserId: parseInt(this.commonService.Decrypt(this.storage.getValue('currentUserId'))),
      PlanId: this.planId,
      PaymentMethodId: this.cardId,
    }

    if (this.cardId == null) {
      this.toster.error("Please select card");
      this.spinner.hide();
      return
    }
    if (this.stripeCustomerId == null || (!this.isActive && this.cancaleDate != null)) {
      this.paymentService.InitializeCustomerSetupandSubscribe(this.subscriptionModel).then((data: any) => {
        this.spinner.hide();
        if (data && data.success) {
          this.commonService.setData(true);
          this.router.navigate(["match"]);
          this.toster.success("You have successfully subscribed " + this.PlanDetails.planName);
        }
        else {
          this.toster.error(data.message);
        }
      }).catch((error: any) => {
        this.toster.error(error.error.message);
        this.spinner.hide();
      })
    }
    else {
      this.subscriptionService.UpgradeSubscription(this.subscriptionModel).then((data: any) => {
        this.spinner.hide();
        if (data && data.success) {
          this.toster.success(data.message);
          this.router.navigate(["match"]);
        }
        else {
          this.toster.error(data.message);
        }
      }).catch((error) => {
        this.toster.error(error.error.message);
        this.spinner.hide();
      })
    }
  }
}
