import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ApplicationLayoutComponent } from './application-layout.component';
import { AuthService } from '../../auth/auth.service';


const routes: Routes = [
    { path: '', component: ApplicationLayoutComponent },
    { path: 'subscription', loadChildren: () => import('../../../../Pages/subscriptions/subscriptions.module').then(m => m.SubscriptionsModule), data: { title: 'Subscriptions' }, canActivate: [AuthService] },
    { path: 'pay/:id', loadChildren: () => import('../../../../Pages/payment/payment.module').then(m => m.PaymentModule), canActivate: [AuthService] },
    
];

@NgModule({
    imports: [RouterModule.forChild(routes)],
    exports: [RouterModule]
})
export class ApplicationLayoutRoutingModule { }
