    import { Injectable } from '@angular/core';

@Injectable({
    providedIn: 'root'
})
export class ApiUrlHelper {
    public apiUrl = {

        subscription: {
            //GetSubscription 
            // Subscription Plan By Id
            GetSubscriptionPlanById: 'Subscription/GetSubscriptionPlanById/{planId}',
            //card token generate
            CreateCustomerandSubscription: 'Subscription/SubscribeUser',
            //get Customer card details
            GetCustomerCards: 'Subscription/GetCustomerCards/{customerId}',
            //add Customer Card
            AddCustomerCard: 'Subscription/AddCustomercard',
            //Remove Customer Card
            RemoveCustomerCard: 'Subscription/RemoveCard/{CardId}',
             //Set Default card
            SetDefaultCard:'Subscription/SetDefaultCard',
            //CancelSubscription
            CancelSubscription:'Subscription/CancelSubscription/{userId}',
            //upgrade Subscription
            UpgradeSubscription :'Subscription/UpdateSubscription',
            // Get Stripe Details By Id
            GetStripeDetailsById :'Subscription/GetStripeDetailsById/{userId}',
            // CreateSetupIntent
            InitiatePayment :"Subscription/CreateSetupIntent" ,
            //ConfirmPayment
            ConfirmPayment : "Subscription/ConfirmPayment"
        }
    };
}
