import { Injectable } from '@angular/core';
import { IndividualConfig, ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root'
})
export class ToastrMessageService {

  constructor(private toastr: ToastrService) { 
    this.toastr.toastrConfig.preventDuplicates = true;
  }

  success(message:string){
    this.toastr.success(message);
  }

  error(message:string){
    this.toastr.error(message);
  }

  warning<ConfigPayload = any>(message?: string, title?: string, override?: Partial<IndividualConfig<ConfigPayload>>){
    this.toastr.warning(message,title,override);
  }

  show(message?: string, title?: string){
    this.toastr.show(message,title)
  }
}
